import java.util.ArrayList;

public class Solution {
    public static String solution() {
        // Q: Repeat the phrase all cars eat gas four times. On the odd times, drop words that start with vowels
        ArrayList<String> result = new ArrayList<>();
        String phrase = "all cars eat gas";
        String[] words = phrase.split(" ");

        for (int i = 0; i < 4; i++) {
            if (i % 2 == 0) {
                // On even times, add all words
                for (String word : words) {
                    result.add(word);
                }
            } else {
                // On odd times, drop words that start with vowels
                for (String word : words) {
                    if (!word.startsWith("a") && !word.startsWith("e")) {
                        result.add(word);
                    }
                }
            }
        }

        String answer = String.join(" ", result);
        return answer;
    }

    public static void main(String[] args) {
        System.out.print(solution());
    }
}
