int main() {
    // Vincent can buy flowers in packages of 3 for $2.50 or in packages of 2 for $1. How much money does he save by buying 18 flowers at the better price?
    int flowers_needed = 18;
    int package_size_3 = 3;
    double price_package_3 = 2.50;
    int package_size_2 = 2;
    double price_package_2 = 1.00;

    // Calculate the cost for 18 flowers using package of 3
    int packages_of_3_needed = flowers_needed / package_size_3;
    int remaining_flowers = flowers_needed % package_size_3;
    double cost_using_package_3 = packages_of_3_needed * price_package_3;
    if (remaining_flowers > 0) {
        cost_using_package_3 += (remaining_flowers / package_size_2 + (remaining_flowers % package_size_2 > 0 ? 1 : 0)) * price_package_2;
    }

    // Calculate the cost for 18 flowers using package of 2
    int packages_of_2_needed = flowers_needed / package_size_2;
    remaining_flowers = flowers_needed % package_size_2;
    double cost_using_package_2 = packages_of_2_needed * price_package_2;
    if (remaining_flowers > 0) {
        cost_using_package_2 += price_package_2; // Add one more package for the remaining flowers
    }

    // Calculate the savings
    double savings = cost_using_package_2 - cost_using_package_3;

    double result = savings;

    std::cout << result;

    return 0;
}
