    int total_cost_4x4 = boards_4x4 * cost_per_4x4;

    int total_cost = total_cost_2x4 + total_cost_4x4;

    int selling_price_per_2x4 = cost_per_2x4 + (cost_per_2x4 * 0.5);
    int selling_price_per_4x4 = cost_per_4x4 + (cost_per_4x4 * 0.5);

    int total_selling_price_2x4 = boards_2x4 * selling_price_per_2x4;
    int total_selling_price_4x4 = boards_4x4 * selling_price_per_4x4;

    int total_selling_price = total_selling_price_2x4 + total_selling_price_4x4;

    int profit = total_selling_price - total_cost;

    std::cout << profit;

    return 0;
}
