#include <iostream>

int main() {
    // Steve and Tim decide to see who can get home from school the fastest. Steve lives further away than Tim, so he is allowed to ride his bike. Steve lives 3 miles from the school and can bike at 440 feet per minute. Tim lives 2 miles away from the school. If Tim can ride his skateboard at 264 feet per minute, how long will the winner be waiting at their house before the loser finishes the race?
    int steve_distance_miles = 3;
    int tim_distance_miles = 2;
    int steve_speed_fpm = 440;
    int tim_speed_fpm = 264;

    // Convert miles to feet (1 mile = 5280 feet)
    int steve_distance_feet = steve_distance_miles * 5280;
    int tim_distance_feet = tim_distance_miles * 5280;

    // Calculate time taken for each to get home (time = distance / speed)
    double steve_time_minutes = static_cast<double>(steve_distance_feet) / steve_speed_fpm;
    double tim_time_minutes = static_cast<double>(tim_distance_feet) / tim_speed_fpm;

    // Calculate the waiting time for the winner
    double waiting_time = steve_time_minutes - tim_time_minutes;
    if (waiting_time < 0) {
        waiting_time = -waiting_time; // Make sure waiting time is positive
    }

    int result = static_cast<int>(waiting_time);

    std::cout << result;

    return 0;
}
