    // Total outcomes = 6
    double probability_greater_than_3 = 3.0 / 6.0; // 50%

    // Probability of rolling an even number: 2, 4, 6 -> 3 outcomes
    // Probability of rolling two even numbers in a row: (3/6) * (3/6)
    double probability_even_two_in_a_row = (3.0 / 6.0) * (3.0 / 6.0); // 25%

    // Difference in probabilities
    double difference = probability_greater_than_3 - probability_even_two_in_a_row; // 50% - 25% = 25%

    // Convert to percentage
    double result = difference * 100; // 25%

    std::cout << result;

    return 0;
}
