    int daily_consumption_initial = 12; // in kilowatts
    int daily_consumption_new_device = 2; // in kilowatts
    int cost_per_kilowatt = 1.50; // in dollars

    // Calculate weekly consumption before adding the new device
    int weekly_consumption_initial = daily_consumption_initial * 7; // in kilowatts
    double weekly_cost_initial = weekly_consumption_initial * cost_per_kilowatt; // in dollars

    // Calculate weekly consumption after adding the new device
    int weekly_consumption_after = (daily_consumption_initial + daily_consumption_new_device) * 7; // in kilowatts
    double weekly_cost_after = weekly_consumption_after * cost_per_kilowatt; // in dollars

    // Calculate the difference in weekly costs
    double cost_difference = weekly_cost_after - weekly_cost_initial;

    double result = cost_difference;

    std::cout << result;

    return 0;
}
