```cpp
int main() {
    // Lorraine and Colleen are trading stickers for buttons. Each large sticker is worth a large button or three small buttons. A small sticker is worth one small button. A large button is worth three small stickers. Lorraine starts with 30 small stickers and 40 large stickers. She trades 90% of her small stickers for large buttons. She trades 50% of her large stickers for large buttons and trades the rest of them for small buttons. How many buttons does she have by the end?
    int small_stickers_initial = 30;
    int large_stickers_initial = 40;

    // Trading 90% of small stickers for large buttons
    int small_stickers_traded = small_stickers_initial * 0.9;
    int large_buttons_from_small = small_stickers_traded / 3; // 3 small stickers for 1 large button

    // Remaining small stickers
    int small_stickers_left = small_stickers_initial - small_stickers_traded;

    // Trading 50% of large stickers for large buttons
    int large_stickers_traded = large_stickers_initial * 0.5;
    int large_buttons_from_large = large_stickers_traded; // 1 large sticker for 1 large button

    // Remaining large stickers
    int large_stickers_left = large_stickers_initial - large_stickers_traded;

    // Trading remaining large stickers for small buttons
    int small_buttons_from_remaining_large = large_stickers_left * 3; // 1 large button for 3 small stickers

    // Total buttons
    int total_buttons = large_buttons_from_small + large_buttons_from_large + small_buttons_from_remaining_large;

    int result = total_buttons;

    std::cout << result;

    return 0;
}
