    int points_per_berry = 5;
    int points_per_second = 30;

    // Let Emma's scores be represented by variables
    int enemies_emma = 0; // Emma's enemies
    int berries_emma = 0; // Emma's berries
    int seconds_emma = 0; // Emma's seconds left

    // Ava's scores
    int enemies_ava_total = enemies_emma + enemies_ava;
    int berries_ava_total = berries_emma + berries_ava;
    int seconds_ava_total = seconds_emma - seconds_difference;

    // Calculate scores
