    int total_age = 100;
    int age_difference = 10; // Paul is 10 years older than John

    // Let John's age be x
    // Then Paul's age is x + age_difference
    // Peter's age is (x + (x + age_difference)) = 2x + age_difference

    // The equation becomes:
    // x + (x + age_difference) + (2x + age_difference) = total_age
    // 4x + 2 * age_difference = total_age
    // 4x + 20 = 100
    // 4x = 80
    // x = 20 (John's age)

    int john_age = (total_age - 2 * age_difference) / 4;
    int paul_age = john_age + age_difference;
    int peter_age = john_age + paul_age;

