#include <iostream>

int main() {
    // Paul is at a train station and is waiting for his train. He isn't sure how long he needs to wait, but he knows that the fourth train scheduled to arrive at the station is the one he needs to get on.
    // The first train is scheduled to arrive in 10 minutes, and this train will stay in the station for 20 minutes.
    int first_train_arrival = 10; // minutes
    int first_train_stay = 20; // minutes

    // The second train arrives half an hour after the first train leaves the station.
    int second_train_arrival = first_train_arrival + first_train_stay + 30; // minutes
    int second_train_stay = first_train_stay / 4; // quarter of the first train's stay

    // The third train arrives an hour after the second train leaves the station.
    int third_train_arrival = second_train_arrival + second_train_stay + 60; // minutes

    // The fourth train will arrive 20 minutes after the third train leaves.
    int fourth_train_arrival = third_train_arrival + 0; // third train leaves immediately after it arrives
    fourth_train_arrival += 20; // minutes

    // Total wait time for Paul is the time from now until the fourth train arrives.
    int total_wait_time = fourth_train_arrival;

    int result = total_wait_time;

    std::cout << result;

    return 0;
}
