    double frying_pan_final_cost = frying_pan_cost * (1 - frying_pan_discount);

    // Calculate the cost of the towels after discount
    double towels_final_cost = towels_cost * (1 - towels_discount);

    // Calculate the new balance after the purchases
    double new_balance = balance_after_return + frying_pan_final_cost + towels_final_cost;

    // Print the new balance
    std::cout << new_balance;

    return 0;
}
