int main() {
    // The Hortex company produces bottled carrot juices. Every day it can produce 4200 bottles of these juices. Each juice can cover 20% of 1 person's daily energy demand. How many more bottles of juices would Hortex have to produce to be able to satisfy 100% of the daily energy needs of 2300 people?
    int bottles_per_day = 4200;
    int energy_coverage_per_bottle = 20; // in percent
    int people = 2300;

    // Total energy demand for 2300 people
    int total_energy_demand = 100; // 100% for each person
    int total_energy_needed = people * total_energy_demand;

    // Total energy covered by the current production
    int total_energy_covered = (bottles_per_day * energy_coverage_per_bottle);

    // Additional energy needed
    int additional_energy_needed = total_energy_needed - total_energy_covered;

    // Since each bottle covers 20%, we need to find out how many more bottles are needed
    int additional_bottles_needed = additional_energy_needed / energy_coverage_per_bottle;

    // If there's a remainder, we need one more bottle
    if (additional_energy_needed % energy_coverage_per_bottle != 0) {
        additional_bottles_needed++;
    }

    int result = additional_bottles_needed;

    std::cout << result;

    return 0;
}
