    int total_pepperoni_slices_needed = friends * pepperoni_slices_per_friend;

    int cheese_pizzas_needed = (total_cheese_slices_needed + cheese_slices_per_pizza - 1) / cheese_slices_per_pizza; // Ceiling division
    int pepperoni_pizzas_needed = (total_pepperoni_slices_needed + pepperoni_slices_per_pizza - 1) / pepperoni_slices_per_pizza; // Ceiling division

    int total_pizzas_needed = cheese_pizzas_needed + pepperoni_pizzas_needed;

    int result = total_pizzas_needed;

    std::cout << result;

    return 0;
}
