int main() {
    // Tim enters a competition and has to try and guess the number of red jelly beans in a jar. 
    // The jar has a square base and is 6 inches by 6 inches and 15 inches tall. 
    // He knows that a jelly bean is .15 cubic inches. 
    // He also knows that they get about 80% efficiency in packing. 
    // He estimates 30% of the jelly beans are red. 
    // The true number of red jelly beans in the container is 900. 
    // How far off was his guess?

    int jar_length = 6;
    int jar_width = 6;
    int jar_height = 15;
    double jelly_bean_volume = 0.15;
    double packing_efficiency = 0.8;
    double red_jelly_bean_percentage = 0.3;
    int true_red_jelly_beans = 900;

    // Calculate the volume of the jar
    double jar_volume = jar_length * jar_width * jar_height;

    // Calculate the effective volume for jelly beans
    double effective_volume = jar_volume * packing_efficiency;

    // Calculate the total number of jelly beans that can fit in the jar
    int total_jelly_beans = effective_volume / jelly_bean_volume;

    // Estimate the number of red jelly beans
    int estimated_red_jelly_beans = total_jelly_beans * red_jelly_bean_percentage;

    // Calculate how far off his guess was
    int difference = estimated_red_jelly_beans - true_red_jelly_beans;

    int result = difference;

    std::cout << result;

    return 0;
}
