int main() {
    // A bus has a capacity of 200 people. When it departed Chengli city, it had 20 people. 
    // On the first stop, 40 people entered the bus. 
    // On the second station, 3/4 of the total number of people who entered the bus at the first station entered the bus. 
    // If the number of passengers on the bus doubled at the third station, calculate the total number of people required to fill the remaining spaces on the bus.

    int bus_capacity = 200;
    int initial_passengers = 20;
    int first_stop_entered = 40;

    // Passengers after the first stop
    int passengers_after_first_stop = initial_passengers + first_stop_entered;

    // Passengers entering at the second stop (3/4 of those who entered at the first stop)
    int second_stop_entered = (3 * first_stop_entered) / 4;
    int passengers_after_second_stop = passengers_after_first_stop + second_stop_entered;

    // Passengers double at the third station
    int passengers_after_third_stop = passengers_after_second_stop * 2;

    // Remaining spaces on the bus
    int remaining_spaces = bus_capacity - passengers_after_third_stop;

    int result = remaining_spaces;

    std::cout << result;

    return 0;
}
