    int visits_per_year = 3;
    int initial_cost_per_visit = 2;
    int years_initial = 5;
    int years_additional = 3;

    // Total visits in the first 5 years
    int total_visits_initial = visits_per_year * years_initial;

    // Cost increase after 5 years
    double cost_increase_percentage = 150.0;
    double increased_cost_per_visit = initial_cost_per_visit * (1 + cost_increase_percentage / 100);

    // Total visits in the additional 3 years
    int total_visits_additional = visits_per_year * years_additional;

    // Total visits
